import React, { Component } from 'react';
import { Input, Button} from 'antd';

const { TextArea } = Input;



class NewText extends Component {
        constructor(props){
        super(props);
        this.state = {
            inpValu:''
        }
    }

    handelChange(e){
        console.log(e)
        console.log(e)
        this.setState({
            inpValu:e.target.value
        })
        console.log(this.state.inpValu)
    }

    render() { 
        return ( 
          <div>  
        <TextArea rows={4}  onChange={this.handelChange.bind(this)}  defaultValue={this.state.inpValu}/> 
        <Button type="link" onClick={yang=>{
    console.log(this.state.inpValu)
}}>buttom</Button>
        </div>
        );
        
    }
  componentDidMount(){}  
}
 
export default NewText;
