  {
    title: '操作',
    dataIndex: '',
    key: 'X',
    render: (text,record,) => <Button type="danger" onClick={key=>{console.log(key.id)}}>删除</Button>,
  },
];
//let _this=this
class OldNew extends Component {
    
      constructor(props) {
      super(props);
      this.state = {
          list : [],
      };
  } 
    componentDidMount(){
       axios.get('http://149.28.94.205:3000/mock/23/user/article/list')
       .then((res)=>{
           console.log(res.data.data)
          // const arr = []
         //  arr.push(res.data.data)
          console.log(res)
           this.setState({
		 			list: res.data.data
		 		},()=>{
  console.log(this.state.list.article_id)
                 }
                 
                 )
           })
       .catch((err)=>{console.log(err)})
    }
    newDele(){
     console.log("success")
   }
    render() { 
        return (   <Table
        rowKey="uid"
    columns={columns}
    dataSource={this.state.list}
  /> );
    }
}
 
export default OldNew;
