import React, { Component } from 'react';
import { Button, Popconfirm, message, Input } from 'antd';
import Editor from 'for-editor'
import { Select } from 'antd';

const { Option } = Select;
class NewText extends Component {
        constructor(props){
        super(props);
        this.state = {
            value:''
        }
        this.$vm = React.createRef()
    }

    // 警示弹出框
    confirm(e) {
    console.log(e);
    message.success('发布成功');
    console.log
    }

    cancel(e) {
    console.log(e);
    message.error('操作取消');
    }
//图片载入变量
  addImg($file) {
    this.$vm.current.$img2Url($file.name, 'file_url')
    console.log($file)
  } 
    handelChange(e){
       // console.log(e)
       // console.log(e.target)
        this.setState({
            value:e
        })
        console.log(this.state.value)
    }
    //发布文章
    newText() {

    }

    render() { 
        return ( 
            <div>
            <div style={{margin: 8}}>
             <Select defaultValue="lucy" style={{ width: 120, margin: 2}}>
      <Option value="jack">前端</Option>
      <Option value="lucy">Java</Option>
      <Option value="disabled">
        全栈
      </Option>
      <Option value="Yiminghe">Python</Option>
    </Select>
    <Input placeholder="请输入标题" style={{width:'400px', margin: 8}}/></div>
         <Editor 
         value={this.state.value} 
         onChange={this.handelChange.bind(this)} 
         addImg={($file) => this.addImg($file)}
          ref={this.$vm}
        height='529px'
          />
          <Popconfirm
    title="是否发布文章?"
    onConfirm={this.confirm.bind(this)}
    onCancel={this.cancel.bind(this)}
    okText="Yes"
    cancelText="No"
  >
  <Button type="primary" style={{float:'right' , margin:'8px 0' }} onClick={this.newText.bind(this)}>发布文章</Button> 
  </Popconfirm>
         
          <Popconfirm
    title="是否保存到草稿箱?"
    onConfirm={this.confirm.bind(this)}
    onCancel={this.cancel.bind(this)}
    okText="Yes"
    cancelText="No"
  >
    <Button style={{ float:'right' , margin:'8px' }}>保存到草稿箱</Button> 
    </Popconfirm>
          </div>
        );
        
    }
  componentDidMount(){}  
}
 
export default NewText;
