import React, { Component } from 'react';
import { Table, Button } from 'antd';
import axios from 'axios';

const columns = [
  { title: '标题', dataIndex: 'name', key: 'name' },
  { title: '最近编辑时间', dataIndex: 'update_time_formatted', key: 'update_time_formatted' },
  { title: '所属分类', dataIndex: 'category', key: 'category' },
  {
    title: '操作',
    dataIndex: '',
    key: 'x',
    render: () => <Button type="danger">删除</Button>,
  },
];

class OldNew extends Component {
      constructor(props) {
      super(props);
      this.state = {
          list : [ {
     article_id: '12345655',//文章ID
    title: '入门到掉发',//标题
    update_time: '1576760626',//时间戳
    update_time_formatted: '2019-12-19 21:03:46',//最近编辑时间
    category: 'JAVA'//所属分类
  }],
      };
  }

    componentDidMount(){
       axios.get('https://wc.gdbylm.com/api/list.php')
       .then((res)=>{
           console.log(res.data)
           console.log(res)
          this.setState({
					list: res.data.data
				})
           })
       .catch((err)=>{console.log(err)})
    }
    render() { 
        return (   <Table
    columns={columns}
    dataSource={this.state.list}
  /> );
    }
}
 
export default OldNew;
